import React from 'react';

interface InvoicePreviewProps {
  date: string;
  recipient: string;
  items: {
    name: string;
    unitPrice: number;
    quantity: number;
  }[];
}

const InvoicePreview: React.FC<InvoicePreviewProps> = ({ date, recipient, items }) => {
  const calculateTotal = () => {
    return items.reduce((total, item) => total + item.unitPrice * item.quantity, 0);
  };

  const subtotal = calculateTotal();
  const tax = subtotal * 0.1; // 10% tax
  const total = subtotal + tax;

  return (
    <div className="invoice-preview">
      <h2>請求書</h2>
      <p>発行日: {date}</p>
      <p>宛名: {recipient}</p>
      <table>
        <thead>
          <tr>
            <th>品名/サービス名</th>
            <th>単価</th>
            <th>数量</th>
            <th>金額</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.unitPrice.toLocaleString()} 円</td>
              <td>{item.quantity}</td>
              <td>{(item.unitPrice * item.quantity).toLocaleString()} 円</td>
            </tr>
          ))}
        </tbody>
      </table>
      <p>小計: {subtotal.toLocaleString()} 円</p>
      <p>消費税 (10%): {tax.toLocaleString()} 円</p>
      <h3>合計金額: {total.toLocaleString()} 円</h3>
    </div>
  );
};

export default InvoicePreview;
