import React, { useState } from 'react';
import InvoiceForm from './components/InvoiceForm';
import InvoicePreview from './components/InvoicePreview';
import './styles/App.css';

const App: React.FC = () => {
    const [invoiceData, setInvoiceData] = useState({
        date: '',
        recipient: '',
        items: [{ name: '', unitPrice: 0, quantity: 0 }],
    });

    const handleDataChange = (data: any) => {
        setInvoiceData(data);
    };

    return (
        <div style={{
            display: 'flex',
            flexDirection: 'row',
            height: '100vh',
            padding: 32,
            boxSizing: 'border-box'
        }}>
            {/* 左側：プレビュー */}
            <div style={{ flex: 1, marginRight: 24, background: '#fafafa', padding: 16, borderRadius: 8, boxShadow: '0 0 8px #eee' }}>
                <InvoicePreview invoiceData={invoiceData} />
            </div>
            {/* 右側：フォーム */}
            <div style={{ flex: 1, background: '#fff', padding: 16, borderRadius: 8, boxShadow: '0 0 8px #eee' }}>
                <h1 style={{ fontSize: 24, marginBottom: 24 }}>請求書作成</h1>
                <InvoiceForm onDataChange={handleDataChange} />
            </div>
        </div>
    );
};

export default App;
