import React, { useRef, useState } from 'react';

function getToday() {
  const today = new Date();
  const yyyy = today.getFullYear();
  const mm = String(today.getMonth() + 1).padStart(2, '0');
  const dd = String(today.getDate()).padStart(2, '0');
  return `${yyyy}-${mm}-${dd}`;
}

type Item = {
  name: string;
  unitPrice: number;
  quantity: number;
};

type Props = {
  onDataChange: (data: { date: string; recipient: string; items: Item[] }) => void;
};

const InvoiceForm: React.FC<Props> = ({ onDataChange }) => {
  const [date, setDate] = useState(getToday());
  const [recipient, setRecipient] = useState('');
  const [items, setItems] = useState<Item[]>([{ name: '', unitPrice: 0, quantity: 0 }]);
  const dateInputRef = useRef<HTMLInputElement>(null);

  const handleItemChange = (index: number, field: keyof Item, value: string | number) => {
    const newItems = [...items];
    if (field === 'name') {
      newItems[index][field] = value as string;
    } else if (field === 'unitPrice' || field === 'quantity') {
      newItems[index][field] = Number(value) as number;
    }
    setItems(newItems);
    onDataChange({ date, recipient, items: newItems });
  };

  const handleAddItem = () => {
    const newItems = [...items, { name: '', unitPrice: 0, quantity: 0 }];
    setItems(newItems);
    onDataChange({ date, recipient, items: newItems });
  };

  const handleChange = (setter: React.Dispatch<React.SetStateAction<any>>, value: any, key: string) => {
    setter(value);
    if (key === 'date') onDataChange({ date: value, recipient, items });
    if (key === 'recipient') onDataChange({ date, recipient: value, items });
  };

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
        <label>
          Êó•‰ªò: 
          <input
            ref={dateInputRef}
            type="date"
            value={date}
            onChange={e => handleChange(setDate, e.target.value, 'date')}
            style={{ marginLeft: 4 }}
          />
        </label>
        <span
          style={{ cursor: 'pointer', fontSize: 22 }}
          title="Êó•‰ªò„ÇíÈÅ∏Êäû"
          onClick={() => dateInputRef.current?.focus()}
        >
          üìÖ
        </span>
      </div>
      <div>
        <label>ÂÆõÂêç: <input type="text" value={recipient} onChange={e => handleChange(setRecipient, e.target.value, 'recipient')} /></label>
      </div>
      <div>
        <h3>È†ÖÁõÆ</h3>
        {items.map((item, idx) => (
          <div key={idx} style={{ marginBottom: 8 }}>
            <input
              type="text"
              placeholder="ÂìÅÂêç"
              value={item.name}
              onChange={e => handleItemChange(idx, 'name', e.target.value)}
              style={{ width: 120, marginRight: 8 }}
            />
            <input
              type="number"
              placeholder="Âçò‰æ°"
              value={item.unitPrice}
              onChange={e => handleItemChange(idx, 'unitPrice', e.target.value)}
              style={{ width: 80, marginRight: 8 }}
            />
            <input
              type="number"
              placeholder="Êï∞Èáè"
              value={item.quantity}
              onChange={e => handleItemChange(idx, 'quantity', e.target.value)}
              style={{ width: 80 }}
            />
          </div>
        ))}
        <button type="button" onClick={handleAddItem}>È†ÖÁõÆ„ÇíËøΩÂä†</button>
      </div>
    </div>
  );
};

export default InvoiceForm;
