import React, { useState } from 'react';
import InvoiceForm from './components/InvoiceForm';
import InvoicePreview from './components/InvoicePreview';
import './styles/App.css';

type InvoiceData = {
    date: string;
    recipient: string;
    recipientSuffix: '様' | '御中';
    items: { name: string; unitPrice: number; quantity: number }[];
};

const App: React.FC = () => {
    const [invoiceData, setInvoiceData] = useState<InvoiceData>({
        date: '',
        recipient: '',
        recipientSuffix: '様',
        items: [{ name: '', unitPrice: 0, quantity: 0 }],
    });

    const handleDataChange = (data: InvoiceData) => {
        setInvoiceData(data);
    };

    // 印刷ボタンの処理
    const handlePrint = () => {
        window.print();
    };

    return (
        <div style={{ height: '100vh', boxSizing: 'border-box', background: '#f5f5f5' }}>
            {/* タイトルバー */}
            <div
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    padding: '24px 24px 12px 24px',
                    borderBottom: '1px solid #e0e6ef',
                    background: '#fff'
                }}
            >
                <h1 style={{
                    margin: 0,
                    fontWeight: 800,
                    fontSize: 28,
                    color: '#1976d2',
                    letterSpacing: 2
                }}>
                    請求書作成
                </h1>
                <button
                    type="button"
                    onClick={handlePrint}
                    className="print-btn"
                    style={{
                        padding: '8px 24px',
                        fontSize: 16,
                        background: '#1976d2',
                        color: '#fff',
                        border: 'none',
                        borderRadius: 6,
                        fontWeight: 700,
                        cursor: 'pointer',
                        boxShadow: '0 2px 8px rgba(25,118,210,0.08)'
                    }}
                >
                    印刷
                </button>
            </div>
            {/* タイトル中央配置 */}
            <div style={{ width: '100%', textAlign: 'center', margin: '32px 0 24px 0' }}>
                <h1 style={{ fontSize: 32, margin: 0, letterSpacing: 2 }}>請求書作成</h1>
            </div>
            {/* 2カラムレイアウト */}
            <div style={{
                display: 'flex',
                flexDirection: 'row',
                height: 'calc(100vh - 120px)',
                padding: '0 32px 32px 32px',
                boxSizing: 'border-box',
                gap: 24,
                justifyContent: 'center',
                alignItems: 'flex-start'
            }}>
                {/* 左側：プレビュー（3分の2） */}
                <div style={{
                    flex: 2,
                    background: '#fff',
                    padding: '32px 24px',
                    borderRadius: 12,
                    boxShadow: '0 4px 24px rgba(0,0,0,0.12), 0 1.5px 4px rgba(0,0,0,0.08)',
                    minWidth: 0
                }}>
                    <InvoicePreview invoiceData={invoiceData} />
                </div>
                {/* 右側：フォーム（3分の1） */}
                <div style={{
                    flex: 1,
                    background: '#fff',
                    padding: 16,
                    borderRadius: 8,
                    boxShadow: '0 0 8px #eee',
                    minWidth: 0
                }}>
                    <InvoiceForm onDataChange={handleDataChange} />
                </div>
            </div>
        </div>
    );
};

export default App;
