import React from 'react';

type Item = {
  name: string;
  unitPrice: number;
  quantity: number;
};

type InvoiceData = {
  date: string;
  recipient: string;
  recipientSuffix: '様' | '御中';
  name?: string;
  address?: string;
  tel?: string;
  items: Item[];
};

const InvoicePreview: React.FC<{ invoiceData: InvoiceData }> = ({ invoiceData }) => {
  // 印刷処理
  const handlePrint = () => {
    window.print();
  };

  // プレースホルダー用
  const placeholderStyle = { color: '#bbb' };

  const subtotal = invoiceData.items.reduce((sum, item) => sum + item.unitPrice * item.quantity, 0);
  const tax = Math.round(subtotal * 0.1);
  const total = subtotal + tax;

  return (
    <div style={{ marginTop: 32, padding: 16 }}>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between', // ← 右端にボタン
        marginBottom: 24
      }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <h2
            style={{
              fontWeight: 700,
              letterSpacing: 2,
              textAlign: 'center',
              margin: 0,
              fontSize: 28
            }}
          >
            請求書
          </h2>
          <span
            className="preview-badge"
            style={{
              marginLeft: 16,
              padding: '2px 10px',
              fontSize: 13,
              color: '#1976d2',
              background: '#e3f0fc',
              borderRadius: 12,
              fontWeight: 600,
              display: 'inline-block'
            }}
          >
            プレビュー
          </span>
        </div>
        {/* 印刷ボタン（印刷時は非表示） */}
        <button
          type="button"
          onClick={handlePrint}
          className="print-btn"
          style={{
            padding: '6px 18px',
            fontSize: 15,
            background: '#1976d2',
            color: '#fff',
            border: 'none',
            borderRadius: 6,
            fontWeight: 600,
            cursor: 'pointer',
            boxShadow: '0 2px 8px rgba(25,118,210,0.08)'
          }}
        >
          印刷
        </button>
      </div>
      {/* 日付 */}
      <div style={{ marginBottom: 8, textAlign: 'right', color: invoiceData.date ? '#222' : '#bbb' }}>
        {invoiceData.date ? invoiceData.date : '日付'}
      </div>
      {/* 宛名 */}
      <div style={{ marginBottom: 8, color: invoiceData.recipient ? '#222' : '#bbb' }}>
        {invoiceData.recipient
          ? <>

              {invoiceData.recipient}
              {invoiceData.recipientSuffix && <span style={{ marginLeft: 4 }}>{invoiceData.recipientSuffix}</span>}
            </>
          : '宛名'}
      </div>
      {/* 名前 */}
      <div style={{ marginBottom: 4, textAlign: 'right', color: invoiceData.name ? '#222' : '#bbb' }}>
        {invoiceData.name ? invoiceData.name : '名前'}
      </div>
      {/* 住所 */}
      <div style={{ marginBottom: 4, textAlign: 'right', color: invoiceData.address ? '#222' : '#bbb' }}>
        {invoiceData.address ? invoiceData.address : '住所'}
      </div>
      {/* 電話番号 */}
      <div style={{ marginBottom: 16, textAlign: 'right', color: invoiceData.tel ? '#222' : '#bbb' }}>
        {invoiceData.tel ? invoiceData.tel : '電話番号'}
      </div>
      <table style={{ width: '100%', marginTop: 8, borderCollapse: 'separate', borderSpacing: 0 }}>
        <thead>
          <tr style={{ background: '#f0f4fa' }}>
            <th style={{
              padding: '10px 8px',
              fontWeight: 700,
              fontSize: 16,
              textAlign: 'left',
              borderRadius: '8px 0 0 0'
            }}>品名</th>
            <th style={{
              padding: '10px 8px',
              fontWeight: 700,
              fontSize: 16,
              textAlign: 'right'
            }}>単価</th>
            <th style={{
              padding: '10px 8px',
              fontWeight: 700,
              fontSize: 16,
              textAlign: 'right'
            }}>数量</th>
            <th style={{
              padding: '10px 8px',
              fontWeight: 700,
              fontSize: 16,
              textAlign: 'right',
              borderRadius: '0 8px 0 0'
            }}>金額</th>
          </tr>
        </thead>
        <tbody>
          {invoiceData.items.map((item, idx) => (
            <tr key={idx} style={{ background: idx % 2 === 0 ? '#fff' : '#f8fafc' }}>
              <td style={{
                padding: '8px 8px',
                fontSize: 15,
                borderBottom: '1px solid #e0e6ef'
              }}>{item.name}</td>
              <td style={{
                padding: '8px 8px',
                textAlign: 'right',
                fontSize: 15,
                borderBottom: '1px solid #e0e6ef'
              }}>{item.unitPrice}</td>
              <td style={{
                padding: '8px 8px',
                textAlign: 'right',
                fontSize: 15,
                borderBottom: '1px solid #e0e6ef'
              }}>{item.quantity}</td>
              <td style={{
                padding: '8px 8px',
                textAlign: 'right',
                fontSize: 15,
                borderBottom: '1px solid #e0e6ef'
              }}>{item.unitPrice * item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ marginTop: 24, textAlign: 'right', fontSize: 16 }}>
        <div>小計: <span style={{ fontWeight: 500 }}>{subtotal} 円</span></div>
        <div>消費税(10%): <span style={{ fontWeight: 500 }}>{tax} 円</span></div>
        <div style={{ fontSize: 18, fontWeight: 700, marginTop: 8 }}>
          合計: {total} 円
        </div>
      </div>
    </div>
  );
};

export default InvoicePreview;
