import React, { useRef, useState } from 'react';
import InvoiceForm from './components/InvoiceForm';
import InvoicePreview from './components/InvoicePreview';
import './styles/App.css';

type InvoiceData = {
    date: string;
    recipient: string;
    recipientSuffix: '様' | '御中';
    items: { name: string; unitPrice: number; quantity: number }[];
};

const App: React.FC = () => {
    const [invoiceData, setInvoiceData] = useState<InvoiceData>({
        date: '',
        recipient: '',
        recipientSuffix: '様',
        items: [{ name: '', unitPrice: 0, quantity: 0 }],
    });
    const [formOpen, setFormOpen] = useState(false);
    const printAreaRef = useRef<HTMLDivElement>(null);

    const handleDataChange = (data: InvoiceData) => setInvoiceData(data);

    return (
        <div className="app-root" style={{ position: 'relative', minHeight: '100vh', background: '#f5f5f5' }}>
            {/* タイトル */}
            <div style={{ width: '100%', textAlign: 'center', margin: '32px 0 24px 0' }}>
                <h1 style={{ fontSize: 32, margin: 0, letterSpacing: 2 }}>請求書作成</h1>
            </div>
            {/* プレビュー中央配置 */}
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: 'calc(100vh - 120px)'
            }}>
                <div>
                    <InvoicePreview invoiceData={invoiceData} />
                    <div style={{ textAlign: 'right', marginTop: 24 }}>
                        <button
                            type="button"
                            onClick={() => setFormOpen(true)}
                            style={{
                                padding: '8px 32px',
                                fontSize: 16,
                                background: '#1976d2',
                                color: '#fff',
                                border: 'none',
                                borderRadius: 6,
                                fontWeight: 700,
                                cursor: 'pointer',
                                boxShadow: '0 2px 8px rgba(25,118,210,0.08)'
                            }}
                        >
                            編集
                        </button>
                    </div>
                </div>
            </div>
            {/* 右側フォーム（スライドイン） */}
            <div className={`slide-form${formOpen ? ' open' : ''}`}>
                <button
                    style={{
                        position: 'absolute',
                        top: 16,
                        right: 24,
                        background: 'none',
                        border: 'none',
                        fontSize: 28,
                        cursor: 'pointer',
                        color: '#1976d2'
                    }}
                    onClick={() => setFormOpen(false)}
                    aria-label="閉じる"
                >×</button>
                <InvoiceForm onDataChange={handleDataChange} />
            </div>
        </div>
    );
};

export default App;
